mapper.select:
select id , username , salt , password , email , gmt_create, auth
from user
where email = #{ email }

select id , username , salt , password , email , gmt_create, auth
from user

    
        id = #{ id }
    
    
        and username = #{ username }
    
    
        and password = #{ password }
    
    
        and email = #{ email }

mapper.update:
update user
set id = #{ id }, username = #{ username }, salt = #{ salt }, password = #{ password }, email = #{ email }, gmt_create = #{ gmtCreate }, auth=#{auth}
